package E_commerce.EcommerceAutomation;

import org.openqa.selenium.By;

import org.testng.Assert;
import org.testng.annotations.Test;

public class AddProductInCartTest extends LaunchAndquit {
	
	@Test
	public void testAddProductInCart() throws InterruptedException {                                                                                                                                                                                                                                                                                           
		HomePage h1 = new HomePage(driver);
		h1.clickOnproductLink();
		
		ProductsPage p1 = new ProductsPage(driver);
		scrollDown();

		p1.hoverOverFirstProduct(driver);
		By addToCartOnModal = By.xpath("(//a[@class ='btn btn-default add-to-cart'])[1]");
		jsClick(addToCartOnModal);
		
		p1.clickOnContinueShopping();
		
		p1.hoverOverSecondProduct(driver);

		By addToCart2 =By.xpath("(//a[@class ='btn btn-default add-to-cart'])[3]");
		jsClick(addToCart2);
		p1.clickOnViewButton();
		
		Assert.assertTrue(( driver.findElement(By.xpath("//a[@class ='btn btn-default check_out']"))).isDisplayed());
		                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
        
		
	}

}
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               